;jQuery.fn.floatheading=function(){var t=this;var opt=(arguments[0]!=undefined)?arguments[0]:{};var pt=(opt.paddingTop!=undefined)?opt.paddingTop:0;$(t).data("w",$(window).width()).data("t",$(t).width());var os={'z-index':$(t).css('z-index'),'overflow':$(t).css('overflow'),'position':$(t).css('position'),'top':$(this).css('top')},ot=$(t).offset().top;$(t).css("width",$(t).width()).css("height",$(t).height());$(window).scroll(function(){if($(this).scrollTop()>=(ot-pt))$(t).css({'z-index':'999','overflow':'visible','position':'fixed','top':pt});else $(t).css(os);});if(arguments[0]!=undefined&&arguments[0].changeWidth!=false)$(window).resize(function(){$(t).css("width",($(t).data("t")+($(window).width()-$(t).data("w"))));});};
